import React, {Component} from 'react';



class Fetchpokemon extends Component {

   constructor(){
   	super();
   	this.state = {
   		pokemonid:[]
   	}
   }

   render(){

   	var {pokemons,urls} = this.props;
	urls.forEach((url)=>{
		fetch(url)
		.then(resp => resp.json())
		.then(info => this.setState({pokemonid:[...this.state.pokemonid,info.id]}))
	})
	return 1;
}
}











export default Fetchpokemon;
