import React, {Component}from 'react';


class App extends Component {
    constructor(){
    	super();
    	this.state = {
         name : ''
    }
    }
componentDidMount(){
	fetch("https://pokeapi.co/api/v2/pokemon/")
	.then(resp => resp.json())
	.then(items =>{
		this.setState({array:items.results.name});
        console.log(items.results);
		});
	console.log(this.state.array);
}

render (){
	return(
	  <div>
		   {this.state.array}  
	  </div>
 );

}	
}


export default App;
