import React,{Component} from 'react';
import Pic from './Pic';
import App from './App';
import './Card.css';





class Card extends Component {

constructor(){
	super()
	this.state={
		escapeclicked:false
	}
}

componentDidMount(){
	document.addEventListener("keydown", this.escFunction, false);
}

escFunction=(event)=>{

    if(event.keyCode === 27) {
         this.setState({escapeclicked:true})
    }
  }

doNothing=()=>{
	return ;
}

	render(){

  if (this.state.escapeclicked){
  	return <App />
  }


 return (
 	<div className = "pic">
 	<Pic  pokemonkey={this.props.pokemonkey} clickFunction={this.doNothing}  />
 	<Chracterstics info = {this.props.info}/>
    </div>
)
}
}




















export default Card;
