import React, {Component} from 'react';



class Fetchpokemon extends Component {

   constructor(){
   	super();
   	this.state = {
   		pokemoninfo:[]
   	}
   }

   render(){

   	var {pokemons,urls} = this.props;
	urls.forEach((url)=>{
		fetch(url)
		.then(resp => resp.json())
		.then(info => this.setState({pokemoninfo:[...this.state.pokemoninfo,info]}))
	})
	return this.state.pokemoninfo;
}
}











export default Fetchpokemon;
