import React, {Component}from 'react';


class App extends Component {
    constructor(){
    	super();
    	this.state = {
         pokemon : [],
         url: []
    }
    }


componentDidMount(){
	fetch("https://pokeapi.co/api/v2/pokemon/")
	.then(resp => resp.json())
	.then(items =>{
        //console.log(items.results);
        items.results.forEach( item => {
            this.setState({pokemon:[...this.state.pokemon,item.name]})
            //this.state.url.concat(item.url)

        })});
}

render (){
	     var pokemons = []
	     for (let i of this.state.pokemon){
	     	pokemons.push(i+'<br>');
	     }
	return (
        <div>
           {pokemons}
        </div>
		)
}	
}


export default App;
