import React,{Component} from 'react';
import Pictures from './Pictures';


class Fetchpokemon extends Component {
    constructor (){
    	super();
    	this.state = {
    		pokemonid : [],
    		abilities:[]
    	}
    }
    componentDidMount(){
        this.props.data.url.forEach((url)=>{
    	fetch(url)
    	.then(resp => resp.json())
    	.then(info=>{this.setState({pokemonid:[...this.state.pokemonid,info.id]})
                     this.setState({abilities:[...this.state.abilities,info.abilities]}) 

          })


    })
    }
    render(){
    	const pokeabilities = this.state.abilities

    if (this.state.pokemonid.length ===20){
    return <Pictures display = {this.state.pokemonid} pokeabilities = {pokeabilities}/>;
}
else{
	return <h1>Loading</h1>
}
}
}



export default Fetchpokemon;
